#https://www.interviewbit.com/problems/vowel-and-consonant-substrings/

# Given a string A consisting of lowercase characters.
# You have to find the number of substrings in A which starts with vowel and end with consonants or vice-versa.
# Return the count of substring modulo pow(10,9) + 7.

class Solution:
    # @param A : string
    # @return an integer
    def solve(self, A):
        strLen = len(A)
        vowels = ['a', 'e', 'i', 'o', 'u']
        
        count = 0
        start = 0
        while start < strLen:
            end = start + 1
            while end < strLen:
                if A[start] in vowels and A[end] not in vowels:
                    count += 1
                
                if A[start] not in vowels and A[end] in vowels:
                    count += 1
                end += 1
            start += 1
        return count % (pow(10,9) + 7)
    
    def solve2(self, A):
        vowels = ['a', 'e', 'i', 'o', 'u']
        vowelCount = 0
        consonantCount = 0
        
        for ch in A:
            if ch in vowels:
                vowelCount += 1
            else:
                consonantCount += 1
        result = 0
        # print(vowelCount)
        # print(consonantCount)
        for i in range(len(A)-1):
            ch = A[i]
            if ch in vowels:
                result += consonantCount
                vowelCount -=1
            else:
                result += vowelCount
                consonantCount -= 1
        
        return result % (pow(10,9) + 7)

sol1 = Solution()
print(sol1.solve2("aba")) 
print(sol1.solve2("a")) 
str1 = "..."
print(sol1.solve2(str1)) 